angular.module('ui.dashboard.GaugeApp').factory('ui.dashboard.GaugeConfiguration',function(){

	var GaugeConfiguration = function(configuration){

		this.id = this._generateID();
		this.max = 100;
		this.decimal = 2;
		this.startAngle = -160;
		this.amplitude = 320;
		this.strokeWidth = 20;
		this.radius = 90;
		//START Rules for multiple gauges
		this.initialValues = [];
		this.numberOfGauge = 1;
		this.radiusRule = {
			apply : true,
			rule : function(value,index,initialRadius,initialWidth){
				return initialRadius - index*initialWidth;
			}
		};
		this.multipleColorRule = {
			apply : true
		};
		this.strokeWidthRule = {
			apply : false,
			rule : function(value,index,initialWidth){
				return initialWidth;
			}
		};
		this.opacityRule = {
			apply : false,
			rule : function(value,index){
				return 1;
			}
		}
		//END Rules for multiple gauges
		this.colors = ['#FFFF00','#FF8C00','#FF4500','#FF0000'];
		this.thresholds = {
			display : true,
			values : [25,55,75],
			strokeWidth : 20,
			amplitude : 2,
			opacity : 1,
			color : 'darkgrey',
			aboveGauge : false
		};
		this.background = {
				display : true,
				color : '#F6F6F6',
				opacity : 1
			};
		this.border = {
				display : true,
				color : 'lightgrey',
				strokeWidth : 2,
				opacity : 1
			};
		this.label = {
			display : true,
			fontsize : 32,
			color : 'black',
			format : function(value){ return value; },
			opacity : 1,
			symbol : {
				display : true,
				fontsize : 20,
				color : 'black',
				format : function(value){ return ''; },
				opacity : 1
			}
		};
		this.title = {
			display : false,
			fontsize : 24,
			value : '',
			color : 'black',
			opacity : 1
		}
		this.transitions = {
			arc : 1000,
			label : 500
		}

		this.update(configuration);
	};

	GaugeConfiguration.prototype._generateID = function(){
	    var id = '';
	    var possible = 'abcdef0123456789';

	    for( var i=0; i < 16; i++ )
	        id += possible.charAt(Math.floor(Math.random() * possible.length));

	    return 'gauge-'+id;
	};

	GaugeConfiguration.prototype._mergeRecursive = function(obj1,obj2){
		for (var p in obj2) {
            try {
                obj1[p] = ( obj2[p].constructor==Object ) ? this._mergeRecursive(obj1[p], obj2[p]) : obj2[p];
            } catch(e) {
                obj1[p] = obj2[p];
            }
        }
        return obj1;
	};

	GaugeConfiguration.prototype.update = function(configuration){
		this._mergeRecursive(this,configuration);
		this.width = this.radius*2 + this.strokeWidth;
		this.height = this.radius*2 + this.strokeWidth;
	};

	GaugeConfiguration.prototype.getColor = function(value){
		for(var i = 0; i < this.thresholds.values.length; i++){
			if(value < this.thresholds.values[i]){
				if(this.colors.length >= i+1){
					return this.colors[i];
				}
			}
		}
		return this.colors[this.colors.length-1];
	};

	return GaugeConfiguration;
});
angular.module('ui.dashboard.GaugeApp').service('ui.dashboard.GaugeService',function(){

	var GaugeService = function(){};

	GaugeService.prototype.polarToCartesian = function(centerX, centerY, radius, angleInDegrees) {
        var angleInRadians = (angleInDegrees-90) * Math.PI / 180.0;
        return {
            x: centerX + (radius * Math.cos(angleInRadians)),
            y: centerY + (radius * Math.sin(angleInRadians))
        };
    };

    GaugeService.prototype.describeArc = function(x, y, radius, startAngle, endAngle){
        var start = this.polarToCartesian(x, y, radius, endAngle);
        var end = this.polarToCartesian(x, y, radius, startAngle);
        var arcSweep = ((endAngle - startAngle) <= 180) ? 0:1;
        return [
            'M', start.x, start.y,
            'A', radius, radius, 0, arcSweep, 0, end.x, end.y
        ].join(' ');
    };

    GaugeService.prototype.computeArc = function(startAngle,endAngle,value,max,width,height,radius){
        return this.describeArc(
                width/2,
                height/2,
                radius,
                startAngle,
                endAngle*(value/max)
            );
    };

    GaugeService.prototype.computeRotation = function(startAngle, width, height){
        return 'rotate(' + startAngle + ',' + width/2 + ',' + height/2 + ')';
    };

	return new GaugeService();
});
